package main

import (
	"fmt"
	"sort"
)

func rangeSum(nums []int, n int, left int, right int) int {
	const MOD = 1_000_000_007
	sorted := make([]int, 0)
	i, j := 0, n-1
	for i <= j {
		sorted = append(sorted, nums[i])
		a := i
		s := len(sorted) - 1
		for a < j {

			sorted = append(sorted, sorted[s]+nums[a+1])
			a++
			s++
		}
		i++
	}
	sort.Slice(sorted, func(i, j int) bool { return sorted[i] < sorted[j] })
	subarray_sum := 0
	for index := left - 1; index <= right-1; index++ {
		subarray_sum = (subarray_sum + sorted[index]) % MOD
	}
	return subarray_sum
}

